--
-- Дамп данных таблицы `archeology`
--

INSERT INTO `archeology` (`id`, `title`, `description`, `image`) VALUES
(9, 'Библейские памятники эпохи кораблей, обнаруженные в израильской пустыне', 'Сохранившись в виде слабых офортов спустя 2000 лет, 13 кораблей украшают стены цистерны в Израиле .<br>\r\n\r\nЦистерна была обнаружена в городе Беэр-Шева в пустыне на юге Израиля, где археологи занимались раскопками в преддверии строительства нового микрорайона. Они наткнулись на углубление в земле, которое оказалось цистерной , большим резервуаром для хранения воды. Бак имеет глубину около 40 футов (12 метров) и ширину около 16 футов на 18 футов (5 на 5,5 м).<br>\r\n\r\nЛестница ведет к нижней части цистерны. Вдоль стен - слабые линии, которые археологи из Управления древностей Израиля (IAA) определили как запечатленные рисунки. Исследователи обнаружили офорты 13 кораблей, одного моряка и нескольких фигур животного типа, называемых зооморфами. <br>\r\n\r\nПо словам специалиста по наскальному искусству IAA Давида Айзенберга-Дегена, корабли очень детализированы. Они реалистичны и показывают детали, соответствующие судостроению того времени. Основываясь на вырубке и оштукатуривании ступеней цистерны и стиле всего водохранилища, археолог пришел к выводу, что цистерна была построена в первом или втором столетии, что делает водохранилище почти 2000 лет.<br>\r\n\r\nЭто было бы в период римского правления в Израиле. Остатки римского поселения лежат на расстоянии около 2600 футов (800 м) от места недавно обнаруженной цистерны, сказал Айзенберг-Деген в заявлении, распространенном МАА. Агентство сообщило, что цистерна будет сохранена как часть зеленой зоны в новом микрорайоне.<br>\r\n\r\nПока неясно, когда цистерна вышла из употребления, но, скорее всего, это было не так давно, сообщает МАА. По данным властей, среди обломков и грязи, обнаруженных на цистерне, были керамические осколки, патроны для снарядов и оружие, относящиеся к первой мировой войне.<br>', '4534NpZW5jZS5jb20vaW1hZ2'),
(8, 'Находки библейской эры 2018 года', 'С признаками человеческого поселения, датируемого как минимум 10 000 лет, Святая Земля является благодатной почвой для археологов. Едва ли проходит месяц без строителей, строящих новую дорогу или жилищное строительство, сталкивающихся с какими-то воспоминаниями о прошлой цивилизации. <br>\r\n\r\nПрошедший год не стал исключением. В 2018 году археологи нашли лицо Иисуса, обнаружили облик древнего царя и, возможно, обнаружили давно потерянное царство. Это истории некоторых из самых интригующих открытий библейской эпохи в этом году.', 'aHR0cDovL3d3dy5saXZlc2NpZW5jZS5jb20vaW1hZ2VzL33333333333333333333'),
(10, 'Туринская плащаница - подделка, кровавые пятна', 'Говорят, что Туринская плащаница является погребальной одеждой Иисуса, а другие - средневековой подделкой. Теперь, новое исследование, использующее современные методы судебной экспертизы, предполагает, что пятна крови на саване абсолютно нереалистичны, подтверждая аргументы, что это подделка.<br>\r\n\r\nТуринская плащаница  является древней льняной ткани длиной около 15 футов 4 футов в ширину (4,4 на 1,1 метра) , который несет на себе образ того , что , как представляется, тело распятого человека. Экспонируемый в соборе Святого Иоанна Крестителя в Турине, Италия, он является одним из многих плащаниц, которые на протяжении веков считались единственной истинной погребальной тканью Иисуса.<br>\r\n\r\nНо в 1988 году ученые датировали происхождение плащаницы углеродом между 1260 и 1390 годами нашей эры , подтверждая утверждения, что это всего лишь обман, поскольку считается, что жизнь Иисуса подошла к концу в 33 году нашей эры. это подделка - все еще горячо обсуждаемый вопрос .<br>', 'aHR0cDovL3d3dy44444'),
(11, 'Это кольцо носит имя человека, который приговорил Иисуса к смерти. Кто на самом деле носил это?', 'На Западном берегу было обнаружено медное перстень, датированный около 2000 лет, с надрезанной греческой надписью «Пилатус».<br>\r\n\r\nОткрытие кольца привело к предположению, что оно могло принадлежать Понтию Пилату (также называемому Понтием Пилатом), римскому префекту Иудеи между 26 и 36 годом н.э., который, согласно Библии, распорядился распятью Иисуса. Тем не менее, группа исследователей, которые проанализировали кольцо, говорят, что оно, вероятно, принадлежало кому-то другому, а не пресловутому римскому префекту.<br>\r\n\r\nКольцо было найдено в 1968-1969 годах во время раскопок, проведенных в Иродиуме, дворце, построенном для царя Ирода (который жил с 74 по 4 год до н.э.), который расположен примерно в 3,1 милях (5 километрах) к юго-востоку от Вифлеема на Западном берегу. Ирод был царем Иудеи, правление которого поддерживалось Римом.<br>', 'aHR0cDovL3d3dy5saXZlc2NpZ323232323');

